  }
  .search-div input {
    background: #f4fdff;
    border: 3px solid #26768d;
    color: #26768d;
    margin-left: 0px;
  }
  .search-div input::placeholder {
    color: #26768d;
  }
  .container {
    .col1 {
      width: 50%;
      height: 500px;
      .foto_mercado {
        width: 190px;
        height: 190px;

        float: left;
      }
      .texto-home {
        color: ${Produtos[0].cor};
        float: left;
        width: 393px;
        span {
          color: #ffae10;
        }
      }
    }
  }
`;
