}

export const Container = styled.div`
  background: #f4fdff;
  #nav-1 li a {
    color: #ffae10;
  }
  #nav-2 a {
    color: #ffae10;
  }
  .search-div input {
    background: #f4fdff;
    border: 3px solid #26768d;
    color: #26768d;
    margin-left: 0px;
  }
  .search-div input::placeholder {
    color: #26768d;
  }
  .container {
    .col1 {
      width: 50%;
      height: 500px;
      .foto_mercado {
        width: 190px;
        height: 190px;
        left: 455px;
        top: 227px;
        border-radius: 100%;

        border: 3px solid #26768d;
        float: left;
      }
      .texto-home {
        color: #26768d;
        float: left;
        width: 393px;
        span {
          color: #ffae10;
        }
      }
    }
  }
`;
